import React, { useState, useEffect } from 'react';
import { Input, Flex, Box, PseudoBox, SimpleGrid } from '@chakra-ui/core';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    number: {
      backgroundColor: 'rgb(100,100,100)',
      padding: '15px 25px',
      borderRadius: '50%',
      color: 'white',
      fontSize: '28px',
    },
  })
);
export default function NumberPad() {
  return (
    <Flex width="300px" align="center" justify="center" height="600px">
      <SimpleGrid columns={3} spacing={2}>
        <Flex width="100px" align="center" justify="center" height="100px">
          <Box
            backgroundColor="rgb(100,100,100)"
            padding="15px 25px"
            borderRadius="50%"
            color="white"
            fontSize="28px"
          >
            1
          </Box>
        </Flex>
        <Box>2</Box>
        <Box>3</Box>
        <Box>4</Box>
        <Box>5</Box>
        <Box>6</Box>
        <Box>7</Box>
        <Box>8</Box>
        <Box>9</Box>
        <Box>0</Box>
      </SimpleGrid>
    </Flex>
  );
}
