import React, { useState, useEffect } from 'react';
import { Input, Flex, Box, PseudoBox, SimpleGrid } from '@chakra-ui/core';

interface IInputPadProps {
  input: string;
}

export default function InputPad(props: IInputPadProps) {
  return (
    <Flex
      width="100%"
      height="40px"
      align="center"
      justify="row-end"
      color="white"
    >
      <SimpleGrid columns={1} spacing={1} width="100%" p={3} fontSize="38px">
        <Box textAlign="right">{props.input}</Box>
      </SimpleGrid>
    </Flex>
  );
}
