import React, { useState, useEffect } from 'react';
import { Input, Flex, Box, PseudoBox, SimpleGrid } from '@chakra-ui/core';

interface IInputPadProps {
  input: string;
}

export default function InputPad(props: IInputPadProps) {
  return (
    <Flex
      width="300px"
      align="center"
      justify="row-end"
      color="white"
      overflow="hidden"
    >
      <SimpleGrid columns={1} spacing={1} width="300px" p={2} fontSize="42px">
        <Box textAlign="right">{props.input}</Box>
      </SimpleGrid>
    </Flex>
  );
}
