import React, { useState, useEffect } from 'react';
import { Input, Flex, Box, PseudoBox, SimpleGrid } from '@chakra-ui/core';

interface IInputPadProps {
  input: number;
}

export default function InputPad(props: IInputPadProps) {
  return (
    <Flex
      width="300px"
      align="center"
      justify="row-end"
      color="white"
      overflow="scroll"
    >
      <SimpleGrid
        columns={1}
        spacing={1}
        width="100%"
        p={2}
        fontSize={props.input.length > 10 ? '32px' : '42px'}
      >
        <Box textAlign="right">{props.input}</Box>
      </SimpleGrid>
    </Flex>
  );
}
