import React, { useState, useEffect } from 'react';
import { Input, Flex, Box, PseudoBox, SimpleGrid } from '@chakra-ui/core';

interface IInputPadProps {
  input: string;
}

export default function InputPad(props: IInputPadProps) {
  let fontSize =
    props.input.toString().length > 11
      ? `${42 - props.input.toString().length}px`
      : '42px';

  const formatInput = (inputStr: string) => {
    const hasE = inputStr.includes('e');
    const hasDot = inputStr.includes('e');
  };

  return (
    <Flex
      // width="300px"
      align="center"
      height="60px"
      color="white"
      overflow="hidden"
    >
      <SimpleGrid
        columns={1}
        spacing={1}
        width="100%"
        p={2}
        fontSize={fontSize}
      >
        <Box textAlign="right">
          {props.input.toString().length <= 22
            ? Math.abs(Number(props.input)) > 999
              ? props.input.toLocaleString()
              : props.input
            : props.input.toString().substr(0, 22)}
        </Box>
      </SimpleGrid>
    </Flex>
  );
}
